package controlStatement;

public class Test {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =99;
		int b = 100;
		System.out.println("("+a+"<"+b+") = "+(a<b));
		System.out.println("("+a+"<="+b+") = "+(a<=b));
		System.out.println("("+a+">"+b+") = "+(a>b));
		System.out.println("("+a+">="+b+") = "+(a>=b));
		System.out.println("("+a+"!="+b+") = "+(a!=b));
	}

}
