package datatype;

public class LongUse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		long price;
		price = 10000000;
		// no need to attach l at end of the value
		long bignumber = 10000000000l;
		//hear attach l at the end
		System.out.println("My flat Price is "+price);
		System.out.println("BigNumber is = "+bignumber);

	}

}
