package logicalProgram;

public class PrintTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 10; i++) {
			for (int j = 2; j <=10; j++) {
				
			
			System.out.printf(" %d * %2d = %2d  ",j,i,i*j);
			}
			
			
		
			System.out.print("\n");
			
		}


	}

}
